    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.3/jquery.min.js"></script>
<script>
    $(document).ready(function(){

        $(document).on('click', '#financelink', function(){
            hidecurencyinfos();
            $("#financeModal").modal('show');
        });

        hidecurencyinfos();
        function hidecurencyinfos(){
            $("#curencyinfos").hide();
        }


        $(document).on('click', '#deposit', function(){
            var getvalue = $("#amount_input").val();
            if(getvalue == ""){
                $("#msg").html('please enter the amount');
            }

            if(getvalue !== ""){
                $("#financeModal").modal('hide');
                $("#selectamount").html(getvalue);
                $("#selectamount1").html(getvalue);
                $("#selectamount2").html(getvalue);
                $("#selectamount3").html(getvalue);
                $("#selectamount4").html(getvalue);
                $("#selectamount5").html(getvalue);
                $("#curencyinfos").show();
            }
        });


        get_costomers();
        function get_costomers(){
            $.ajax({
                type: "GET",
                url: "{{ route('users.listcostomers') }}",
                dataType: "json",
                success: function (data) {
                    if(data.error){
                        $('#clientlist').html('<option disabled>'+data.error+'</option>');
                    }

                    if(data.success){
                        $.each(data.success, function(key, value) {
                            $('#clientlist').append('<option value='+value.matricule+'>'+value.nom+' '+value.prenom+' '+value.email+'</option>');
                        });
                    }

                }
            });
        }



        get_transactions();
        function get_transactions(){
            $.ajax({
                type: "GET",
                url: "{{ route('users.getalltransaction') }}",
                dataType: "json",
                success: function (data) {
                    if(data.error){
                        $('#alltransaction').html('<tr><th colspan="6">'+data.error+'</th></tr>');
                    }

                    if(data.success){
                        $.each(data.success, function(key, value) {
                            $('#alltransaction').append('<tr><td>'+value.matricule+'</td><td>Inv</td><td>'+value.montant+'</td><td>'+value.pourcentage+' %</td><td>'+value.fluctuation+'</td><td></td><td>'+value.created_at+'</td><td>completed</td><td>'+value.nom+' '+value.prenom+'</td><td></button></div><div class="col-md-1"><button class="btn  deletetranc" id="'+value.matricule+'">X</button></td></tr>');
                        });
                    }

                }
            });
        }


        get_transactions_costomers();
        function get_transactions_costomers(){
            $.ajax({
                type: "GET",
                url: "{{ route('users.getalltransactioncostomers') }}",
                dataType: "json",
                success: function (data) {
                    if(data.error){
                        $('#alltransactioncostomers').html('<tr><th colspan="6">'+data.error+'</th></tr>');
                    }

                    if(data.success){
                        var balance = 0;
                        $.each(data.success, function(key, value) {
                            var montant = value.fluctuation;
                            balance = (parseFloat(balance) + parseFloat(montant));
                            $('#alltransactioncostomers').append('<tr><td>'+value.matricule+'</td><td>Inv</td><td>'+numberformat(value.montant)+' USD</td><td>'+numberformat(value.pourcentage)+' %</td><td>'+numberformat(value.fluctuation)+' USD</td><td></td><td>'+value.created_at+'</td><td>completed</td></tr>');
                        });

                        $('#costomersbalance').html(' ___ '+numberformat(balance)+' ___ ');
                    }

                }
            });
        }
        function numberformat(number){
            return new Intl.NumberFormat("en-IN").format(number)
        }

        $('#new_transaction').on('submit', function(event){
            event.preventDefault();

            var _token = $("input[name='_token']").val();
            var clientlist = $('#clientlist').find(":selected").val();
            var amount = $("input[name='amount']").val();
            var fluctuation = $("input[name='fluctuation']").val();

            $.ajax({
                type: "POST",
                url: "{{ route('users.newtransaction') }}",
                data: {
                    _token : _token,
                    clientlist: clientlist,
                    amount: amount,
                    fluctuation:fluctuation
                },
                dataType: "json",
                beforeSend:function(){
                    $('#btn_create_transaction').attr('disabled', 'disabled');
                },
                success: function (data) {

                    if(data.success) {
                        $('#alltransaction').empty();
                        $('.alert-danger').empty();
                        $('.alert-danger').html('Transaction created successfully');
                        get_transactions();
                        get_transactions_costomers();
                        $("#new_transaction")[0].reset();
                    }
                    if(data.error) {
                        $('.alert-danger').empty();
                        $('.alert-danger').html('Transaction not created successfully');
                    }

                    if(data.errors){
                        $('.alert-danger').empty();
                        $.each(data.errors, function(key, value) {
                            $('.alert-danger').append('<p>'+value+'</p>');
                        });
                    }

                    $('#btn_create_transaction').attr('disabled', false);
                }

            });

        });



        $(document).on('click', '.deletetranc', function(){
            var mat_ = $(this).attr("id");
            var _token = $("input[name='_token']").val();
            $.ajax({
                type: "POST",
                url: "{{ route('users.deltransaction') }}",
                data: {
                    _token : _token,
                    mat_: mat_
                },
                dataType: "json",
                success: function (data) {
                    if(data.success) {
                        $('.alert-primary').empty();
                        $('.alert-primary').html(data.success);
                        get_transactions();
                    }
                    if(data.error) {
                        $('.alert-primary').empty();
                        $('.alert-primary').html(data.error);
                    }
                }

            });
        });


        $(document).on('click', '.amount', function(){
            var mat = $(this).attr("id");
            $('#amount_input').val(mat);
        });


    });


</script>
